#define LOGI(FORMAT,...) __android_log_print(ANDROID_LOG_INFO,&quot;FFMPEG&quot;,FORMAT,##__VA_ARGS__);
#define LOGE(FORMAT,...) __android_log_print(ANDROID_LOG_ERROR,&quot;FFMPEG&quot;,FORMAT,##__VA_ARGS__);


&#x2F;*
 * Class:     cn_onestravel_ndk_ffmpegdecode_VoideUtils
 * Method:    decode
 * Signature: (Ljava&#x2F;lang&#x2F;String;Ljava&#x2F;lang&#x2F;String;)V
 *&#x2F;
JNIEXPORT void JNICALL Java_cn_onestravel_ndk_ffmpegdecodedemo_VideoUtils_decode
  (JNIEnv * env, jclass jcls, jstring input_jstr, jstring output_jstr)&#123;
  	&#x2F;&#x2F;需要转码的视频文件(输入的视频文件)
  	const char* input_cstr &#x3D; (*env)-&gt;GetStringUTFChars(env,input_jstr,NULL);
  	const char* output_cstr &#x3D; (*env)-&gt;GetStringUTFChars(env,output_jstr,NULL);

  	&#x2F;&#x2F;1.注册所有组件
  	av_register_all();

  	&#x2F;&#x2F;封装格式上下文，统领全局的结构体，保存了视频文件封装格式的相关信息
  	AVFormatContext *pFormatCtx &#x3D; avformat_alloc_context();

  	&#x2F;&#x2F;2.打开输入视频文件
  	if (avformat_open_input(&amp;pFormatCtx, input_cstr, NULL, NULL) !&#x3D; 0)
  	&#123;
  		LOGE(&quot;%s&quot;,&quot;无法打开输入视频文件&quot;);
  		return;
  	&#125;

  	&#x2F;&#x2F;3.获取视频文件信息
  	if (avformat_find_stream_info(pFormatCtx,NULL) &lt; 0)
  	&#123;
  		LOGE(&quot;%s&quot;,&quot;无法获取视频文件信息&quot;);
  		return;
  	&#125;

  	&#x2F;&#x2F;获取视频流的索引位置
  	&#x2F;&#x2F;遍历所有类型的流（音频流、视频流、字幕流），找到视频流
  	int v_stream_idx &#x3D; -1;
  	int i &#x3D; 0;
  	&#x2F;&#x2F;number of streams
  	for (; i &lt; pFormatCtx-&gt;nb_streams; i++)
  	&#123;
  		&#x2F;&#x2F;流的类型
  		if (pFormatCtx-&gt;streams[i]-&gt;codec-&gt;codec_type &#x3D;&#x3D; AVMEDIA_TYPE_VIDEO)
  		&#123;
  			v_stream_idx &#x3D; i;
  			break;
  		&#125;
  	&#125;

  	if (v_stream_idx &#x3D;&#x3D; -1)
  	&#123;
  		LOGE(&quot;%s&quot;,&quot;找不到视频流\n&quot;);
  		return;
  	&#125;

  	&#x2F;&#x2F;只有知道视频的编码方式，才能够根据编码方式去找到解码器
  	&#x2F;&#x2F;获取视频流中的编解码上下文
  	AVCodecContext *pCodecCtx &#x3D; pFormatCtx-&gt;streams[v_stream_idx]-&gt;codec;
  	&#x2F;&#x2F;4.根据编解码上下文中的编码id查找对应的解码
  	AVCodec *pCodec &#x3D; avcodec_find_decoder(pCodecCtx-&gt;codec_id);
  	&#x2F;&#x2F;（迅雷看看，找不到解码器，临时下载一个解码器）
  	if (pCodec &#x3D;&#x3D; NULL)
  	&#123;
  		LOGE(&quot;%s&quot;,&quot;找不到解码器\n&quot;);
  		return;
  	&#125;

  	&#x2F;&#x2F;5.打开解码器
  	if (avcodec_open2(pCodecCtx,pCodec,NULL)&lt;0)
  	&#123;
  		LOGE(&quot;%s&quot;,&quot;解码器无法打开\n&quot;);
  		return;
  	&#125;

  	&#x2F;&#x2F;输出视频信息
  	LOGI(&quot;视频的文件格式：%s&quot;,pFormatCtx-&gt;iformat-&gt;name);
  	LOGI(&quot;视频时长：%ld&quot;, (pFormatCtx-&gt;duration)&#x2F;1000000);
  	LOGI(&quot;视频的宽高：%d,%d&quot;,pCodecCtx-&gt;width,pCodecCtx-&gt;height);
  	LOGI(&quot;解码器的名称：%s&quot;,pCodec-&gt;name);

  	&#x2F;&#x2F;准备读取
  	&#x2F;&#x2F;AVPacket用于存储一帧一帧的压缩数据（H264）
  	&#x2F;&#x2F;缓冲区，开辟空间
  	AVPacket *packet &#x3D; (AVPacket*)av_malloc(sizeof(AVPacket));

  	&#x2F;&#x2F;AVFrame用于存储解码后的像素数据(YUV)
  	&#x2F;&#x2F;内存分配
  	AVFrame *pFrame &#x3D; av_frame_alloc();
  	&#x2F;&#x2F;YUV420
  	AVFrame *pFrameYUV &#x3D; av_frame_alloc();
  	&#x2F;&#x2F;只有指定了AVFrame的像素格式、画面大小才能真正分配内存
  	&#x2F;&#x2F;缓冲区分配内存
  	uint8_t *out_buffer &#x3D; (uint8_t *)av_malloc(avpicture_get_size(AV_PIX_FMT_YUV420P, pCodecCtx-&gt;width, pCodecCtx-&gt;height));
  	&#x2F;&#x2F;初始化缓冲区
  	avpicture_fill((AVPicture *)pFrameYUV, out_buffer, AV_PIX_FMT_YUV420P, pCodecCtx-&gt;width, pCodecCtx-&gt;height);

  	&#x2F;&#x2F;用于转码（缩放）的参数，转之前的宽高，转之后的宽高，格式等
  	struct SwsContext *sws_ctx &#x3D; sws_getContext(pCodecCtx-&gt;width,pCodecCtx-&gt;height,pCodecCtx-&gt;pix_fmt,
  		pCodecCtx-&gt;width, pCodecCtx-&gt;height, AV_PIX_FMT_YUV420P,
  		SWS_BICUBIC, NULL, NULL, NULL);


  	int got_picture, ret;

  	FILE *fp_yuv &#x3D; fopen(output_cstr, &quot;wb+&quot;);

  	int frame_count &#x3D; 0;

  	&#x2F;&#x2F;6.一帧一帧的读取压缩数据
  	while (av_read_frame(pFormatCtx, packet) &gt;&#x3D; 0)
  	&#123;
  		&#x2F;&#x2F;只要视频压缩数据（根据流的索引位置判断）
  		if (packet-&gt;stream_index &#x3D;&#x3D; v_stream_idx)
  		&#123;
  			&#x2F;&#x2F;7.解码一帧视频压缩数据，得到视频像素数据
  			ret &#x3D; avcodec_decode_video2(pCodecCtx, pFrame, &amp;got_picture, packet);
  			if (ret &lt; 0)
  			&#123;
  				LOGE(&quot;%s&quot;,&quot;解码错误&quot;);
  				return;
  			&#125;

  			&#x2F;&#x2F;为0说明解码完成，非0正在解码
  			if (got_picture)
  			&#123;
  				&#x2F;&#x2F;AVFrame转为像素格式YUV420，宽高
  				&#x2F;&#x2F;2 6输入、输出数据
  				&#x2F;&#x2F;3 7输入、输出画面一行的数据的大小 AVFrame 转换是一行一行转换的
  				&#x2F;&#x2F;4 输入数据第一列要转码的位置 从0开始
  				&#x2F;&#x2F;5 输入画面的高度
  				sws_scale(sws_ctx, pFrame-&gt;data, pFrame-&gt;linesize, 0, pCodecCtx-&gt;height,
  					pFrameYUV-&gt;data, pFrameYUV-&gt;linesize);

  				&#x2F;&#x2F;输出到YUV文件
  				&#x2F;&#x2F;AVFrame像素帧写入文件
  				&#x2F;&#x2F;data解码后的图像像素数据（音频采样数据）
  				&#x2F;&#x2F;Y 亮度 UV 色度（压缩了） 人对亮度更加敏感
  				&#x2F;&#x2F;U V 个数是Y的1&#x2F;4
  				int y_size &#x3D; pCodecCtx-&gt;width * pCodecCtx-&gt;height;
  				fwrite(pFrameYUV-&gt;data[0], 1, y_size, fp_yuv);
  				fwrite(pFrameYUV-&gt;data[1], 1, y_size &#x2F; 4, fp_yuv);
  				fwrite(pFrameYUV-&gt;data[2], 1, y_size &#x2F; 4, fp_yuv);

  				frame_count++;
  				LOGI(&quot;解码第%d帧&quot;,frame_count);
  			&#125;
  		&#125;

  		&#x2F;&#x2F;释放资源
  		av_free_packet(packet);
  	&#125;

  	fclose(fp_yuv);

  	(*env)-&gt;ReleaseStringUTFChars(env,input_jstr,input_cstr);
  	(*env)-&gt;ReleaseStringUTFChars(env,output_jstr,output_cstr);

  	av_frame_free(&amp;pFrame);

  	avcodec_close(pCodecCtx);

