/**
 * 循环求斐波那契数列
 *
 * @param n
 * @return
 */
public int fibonacci(int n) &#123;
    int a = 1, b = 1, c = 0;
    if (n == 0) &#123;
        return 0;
    &#125; else if (n == 1 || n == 2) &#123;
        return 1;
    &#125; else &#123;
        for (int i = 3; i &lt;= n; i++) &#123;
            c = a + b;
            a = b;
            b = c;
        &#125;
    &#125;
    return c;
&#125;

