/**
 * 采用矩阵的解法
 * 时间复杂度为O(logN)
 *
 * @param n
 * @return
 */
public int fibonacci(int n) &#123;
    if (n == 0) &#123;
        return 0;
    &#125;
    int[][] fbnq = fbnq(n);
    return fbnq[1][0];
&#125;

/**
 * 单元矩阵
 */
private static final int[][] UNIT = &#123;&#123;1, 1&#125;, &#123;1, 0&#125;&#125;;

/**
 * 矩阵求斐波那契
 *
 * @param n
 * @return
 */
private int[][] fbnq(int n) &#123;
    if (n == 1) &#123;
        return UNIT;
    &#125;
    if (n % 2 == 0) &#123;
        int[][] matrix = fbnq(n / 2);
        //相当于矩阵的n次幂(A^n/2  * A^n/2 = A^(n/2+n/2) = A^n
        return matrixMultiply(matrix, matrix);
    &#125; else &#123;
        int[][] matrix = fbnq((n - 1) / 2);
        //相当于矩阵的n次幂(A^(n-1)/2  * A^(n-1)/2 = A^((n-1)/2+(n-1)/2+1) = A^n
        return matrixMultiply(UNIT, matrixMultiply(matrix, matrix));
    &#125;
&#125;

/**
 * 矩阵乘法
 *
 * @param a 相乘的矩阵a
 * @param b 相乘的矩阵b
 * @return 矩阵相乘的结果
 */
private static int[][] matrixMultiply(int[][] a, int[][] b) &#123;
    int rows = a.length;
    int cols = b[0].length;
    int[][] matrix = new int[rows][cols];
    for (int i = 0; i &lt; a.length; i++) &#123;
        for (int j = 0; j &lt; b[0].length; j++) &#123;
            for (int k = 0; k &lt; a[i].length; k++) &#123;
                matrix[i][j] += a[i][k] * b[k][j];
            &#125;
        &#125;
    &#125;
    return matrix;
&#125;

public static void main(String[] args)&#123;
    System.out.println(fibonacci(6));
&#125;
