    LinkedNode&lt;Integer> two = new LinkedNode&lt;>();
    two.setValue(2);
    LinkedNode&lt;Integer> three = new LinkedNode&lt;>();
    three.setValue(3);
    two.setNext(three);
    first.setNext(two);

    System.out.println(printReverseLinkToHead(first));
