private ArrayList&lt;Integer> printReverseLinkToHeadWithStack(LinkedNode&lt;Integer> node)&#123;
    Stack&lt;Integer> stack = new Stack&lt;>();
    while (node != null) &#123;
        stack.add(node.getValue());
        node = node.next;
    &#125;
    ArrayList&lt;Integer> list = new ArrayList();
    while (!stack.isEmpty()) &#123;
        list.add(stack.pop());
    &#125;
    return list;
&#125;

