     * 设置Observable的工作线程
     * @param observable
     * @param &lt;T>
     * @return
    &lt;/T> */
    fun &lt;T> observe(observable: Observable&lt;T>): Observable&lt;T> &#123;
        return observable.subscribeOn(Schedulers.io())
            .unsubscribeOn(Schedulers.io())
            .observeOn(AndroidSchedulers.mainThread())
