	&#123;
		&#x2F;&#x2F;释放旧的内存
		if (this-&gt;name !&#x3D; NULL)
		&#123;
			delete[] this-&gt;name;
			this-&gt;name &#x3D; NULL;
			this-&gt;age &#x3D; 0;
		&#125;

		&#x2F;&#x2F;重新分配
		this-&gt;name &#x3D; new char[strlen(obj.name) + 1];
		strcpy(this-&gt;name, obj.name);
		this-&gt;age &#x3D; obj.age;

		return *this;
	&#125;

public:
	char* name;
	int age;
&#125;;

void func()
&#123;
	vector&lt;Employee&gt; v1;
	Employee e1(const_cast&lt;char*&gt;(&quot;jack&quot;), 20);
	v1.push_back(e1);
&#125;

void main()
&#123;
	&#x2F;&#x2F;vector&lt;Employee&gt; v1;
	&#x2F;&#x2F;Employee e1(&quot;jack&quot;,20);
	&#x2F;&#x2F;将e1拷贝到vector中
	&#x2F;&#x2F;v1.push_back(e1);

