    struct JNINativeInferface_ struct_env;
    struct_env.NewStringUTF &#x3D; NewStringUTF;

    &#x2F;&#x2F;结构体指针
    JNIEnv e &#x3D; &amp;struct_env;
    &#x2F;&#x2F;结构体的二级指针
    JNIEnv *env &#x3D; &amp;e;
    &#x2F;&#x2F;通过二级指针调用函数
    char* str &#x3D; (*env)-&gt;NewStringUTF(env,&quot;Hello&quot;);
